void Controller::getManifest() {
  String content = "";

  content += "{";
  content += "\"background_color\": \"#FFFFFF\",";
  content += "\"description\": \"Alex WortUhr\",";
  content += "\"dir\": \"ltr\",";
  content += "\"display\": \"standalone\",";
  content += "\"icons\": [{\"sizes\": \"128x128\", \"src\": \"data:image/png;base64,\", \"type\": \"image/png\"}],";
  content += "\"lang\": \"en-US\",";
  content += "\"name\": \"Alex WortUhr\",";
  content += "\"short_name\": \"WortUhr\",";
  content += "\"start_url\": \"/\",";
  content += "\"theme_color\": \"#3d72a8\"";
  content += "}";

  WebServer::httpwebserver.send(200, "application/json", content);
}

void Controller::notFound() {
  if(WebServer::httpwebserver.method() == HTTP_OPTIONS) {
    WebServer::httpwebserver.sendHeader("Allow", "DELETE,GET,HEAD,OPTIONS,POST,PUT");
    WebServer::httpwebserver.send(200, "text/html", "");

    return;
  }

  WebServer::httpwebserver.send(404, "text/html", "NOT FOUND");
}
